import React, { useEffect, useState } from 'react'
import { Outlet } from 'react-router-dom';
import NavBar from './NavBar'



const RootLayout= ({firstName, lastName, logoutKey, key}) => {

  React.useEffect(() => {
    // No need to do anything here, just using logoutKey as a dependency
    // React will re-render NavBar whenever logoutKey changes
  }, [key]);

  return (
    <div>

        <header>
        <NavBar firstName={firstName} lastName={lastName} logoutKey={logoutKey}/>
        </header>

        <main>
            <Outlet />
        </main>
    </div>
  )
};


export default RootLayout;
