import React, { useState, useContext } from "react";
import JoblyContext from "../JoblyContext";
// import JoblyApi from "../JoblyApi";

const Login = () => {
    const JoblyApi = useContext(JoblyContext); // Access the context

    const initialState = {
        username: "",
        password: ""
        }

    const [loginData, setLoginData] = useState(initialState);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setLoginData((prevData) => ({
          ...prevData,
          [name]: value
        }));
      };



    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            // Call findUser function from JoblyApi class to verify credentials
            const user = await JoblyApi.findUser(loginData);
            console.log("Login successful!");
        } catch (error) {
            // If there's an error (e.g., invalid credentials), handle it
            console.error("Error:", error.message);
        }
        console.log(loginData)
    };

    return (
        <div>
            <section className="section-container">
                <h1 className="title-container">Login</h1>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Username:</label>
                        <input
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <div>
                        <button type="submit">Login</button>
                    </div>
                    {error && <div style={{ color: 'red' }}>{error}</div>}
                </form>
            </section>
        </div>
    );
};

export default Login;
