import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import JoblyContext from "../JoblyContext";
import useConfirmLoggedIn from "../Helpers/useConfirmLoggedIn";

const ProfileEditPage = () => {
    useConfirmLoggedIn();

    const JoblyApi = useContext(JoblyContext)
    const navigate = useNavigate();
    const [userData, setUserData] = useState({
        username: "",
        first_name: "",
        last_name: "",
        email: "",
        password: "",
        error: ""
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Call the updateUser method from JoblyApi with updated user data
            await JoblyApi.updateUser(userData);
            // Redirect to profile page after successful update
            navigate("/profile");
        } catch (error) {
            setUserData((prevData) => ({
                ...prevData,
                error: error.message
            }));
        }
    };

    return (
        <div>
            <section className="section-container"> 
                <form className="form-container" onSubmit={handleSubmit}>
                    <div className="form-item">
                        <label htmlFor="first_name">First Name</label> 
                        <input 
                            id="first_name" 
                            type="text" 
                            name="first_name" 
                            value={userData.first_name} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="last_name">Last Name</label> 
                        <input 
                            id="last_name" 
                            type="text" 
                            name="last_name" 
                            value={userData.last_name} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="email">Email</label> 
                        <input 
                            id="email" 
                            type="text" 
                            name="email" 
                            value={userData.email} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="password">Password</label> 
                        <input 
                            id="password" 
                            type="password" 
                            name="password" 
                            value={userData.password} 
                            onChange={handleChange} />
                    </div>
                    {/* Add other fields (firstName, lastName, email) similarly */}
                    <button type="submit">Update</button>
                </form>
                {userData.error && (
                    <p>{userData.error}</p>
                )}
            </section>
        </div>
    );
};

export default ProfileEditPage;
