import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import JoblyContext from "../JoblyContext";

const Logout = ({ loginKey }) => {
    const JoblyApi = useContext(JoblyContext);
    const navigate = useNavigate();

    const initialState = {
        response: localStorage.getItem("username") || "",
        error: ""
    };

    const [confirmLogout, setConfirmLogout] = useState(initialState);
    const [formSubmitted, setFormSubmitted] = useState(false); // New state


    const handleChange = (e) => {
        const { name, value } = e.target;
        setConfirmLogout((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    useEffect(() => {
        // Update localStorage when response changes
        localStorage.setItem("response", confirmLogout.response);
    }, [confirmLogout.response]);


    const handleSubmit = async (event) => {
        event.preventDefault();
        try {        
            await JoblyApi.findUser(confirmLogout);
            localStorage.removeItem('currentUser');
            navigate('/logoutconfirmation'); // Redirect after successful logout
            loginKey(); // Update the key in the parent component
            setFormSubmitted(true); // Set formSubmitted to true after successful submission
        } catch (error) {
            setConfirmLogout((prevData) => ({
                ...prevData,
                error: "Error logging out"
            }));
        }
    };

    return (
        <div>
            <section className="section-container"> 
                <form className="form-container" onSubmit={handleSubmit}>
                    <div className="form-item">
                        <label htmlFor="Response">How was it?</label>
                        <input 
                            id="response" 
                            type="text" 
                            name="response"
                            value={confirmLogout.response} 
                            onChange={handleChange} />
                    </div>
                    <button type="submit">Logout</button>
                </form>
                {formSubmitted && (
                    <p>Form submitted successfully!</p>
                )}
            </section>
        </div>
    );
};

export default Logout;
