import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./NavBar.css";

const NavBar = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");

    useEffect(() => {
        // Retrieve user data from localStorage
        const currentUserJSON = localStorage.getItem('currentUser');
        if (currentUserJSON) {
            const currentUser = JSON.parse(currentUserJSON);
            // Extract first_name and last_name from user data
            setFirstName(currentUser.first_name);
            setLastName(currentUser.last_name);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('currentUser');
    };

    return (
        <nav className="navbar">
            <ul className="navbar-list">
                <li className="navbar-item">
                    {firstName && lastName && (
                        <p className="title-container">{lastName}, {firstName}</p>
                    )}
                </li>
                <li className="navbar-item">
                    <Link to="/">Homepage</Link>
                </li>
                <li className="navbar-item">
                    <Link to="/companies">Companies</Link>
                </li>
                <li className="navbar-item">
                    <Link to="/jobs">Jobs</Link>
                </li>
                <li className="navbar-item">
                    <Link to="/login">Login</Link>
                </li>
                <li className="navbar-item">
                    <Link to="/signup">Signup</Link>
                </li>
                <li className="navbar-item">
                    <Link to="/users">Users</Link>
                </li>
                <li className="navbar-item">
                    <Link to="/profileeditpage">Profile Edit Page</Link>
                </li>
                <li className="navbar-item">
                    <Link to="/logoutconfirmation" onClick={handleLogout}>Logout</Link>
                </li>
            </ul>
        </nav>
    );
};

export default NavBar;
