import React, { useState, useContext } from "react";
import JoblyContext from "../JoblyContext";
import { redirect } from "react-router-dom";
// import JoblyApi from "../JoblyApi";

const Login = () => {
    const JoblyApi = useContext(JoblyContext); // Access the context

    const initialState = {
        username: "",
        password: ""
        }

    const [loginData, setLoginData] = useState(initialState);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setLoginData((prevData) => ({
          ...prevData,
          [name]: value
        }));
      };

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            // Call findUser function from JoblyApi class to verify credentials
            await JoblyApi.findUser(loginData);
            console.log("Login successful!");
            <Redirect to="/" replace />;
        } catch (error) {
            // If there's an error (e.g., invalid credentials), handle it
            console.error("Error:", error.message);
        }
        setLoginData(initialState);
    };

    return (
        <div>
        <section className="section-container">   

        <h1 className="title-container">Login</h1>     

        <div>
  <form className="form-container" onSubmit={handleSubmit}>
    <div className="form-item">
      <label htmlFor="username">Username:</label>
      <input 
        id="username" 
        type="text" 
        name="username"
        value={loginData.username} 
        onChange={handleChange} />
    </div>

    <div className="form-item">
      <label htmlFor="password">Password:</label>
      <input 
        id="password" 
        type="password" 
        name="password"
        value={loginData.password} 
        onChange={handleChange} />
    </div>

    <button type="submit">Submit</button>       
  </form>
</div>



        </section>

        </div>
    );
};

export default Login;
