import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Logout = ({ logoutKey }) => {
    const initialState = {
        response: "",
        error: ""
    };

    const [confirmLogout, setConfirmLogout] = useState(initialState);
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setConfirmLogout((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };


    const handleSubmit = async (event) => {
        try{
        console.log("Before", confirmLogout)
        event.preventDefault();
        localStorage.removeItem('currentUser');
        setConfirmLogout(null);
        navigate('/logoutconfirmation'); // Redirect after successful logout
        logoutKey(); // Update the key in the parent component
        } catch (error) {
            console.error("Error logging out", error)
        } 
        setConfirmLogout((prevData) => ({
            ...prevData,
            response: ""
        }));
    };


    return (
        <div>
            <section className="section-container"> 
                <form className="form-container" onSubmit={handleSubmit}>
                <div className="form-item">
                    <label htmlFor="Response">How was it?</label>
                    <input 
                        id="response" 
                        type="text" 
                        name="response"
                        value={confirmLogout.response} 
                        onChange={handleChange} />
                        </div>
                    <button type="submit">Logout</button>
                </form>
            </section>
        </div>
    );
};

export default Logout;
