import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./NavBar.css";

const NavBar = ({ firstName, lastName, updateKey }) => {
    const [isLoggedIn, setIsLoggedIn] = useState(!!(firstName && lastName));

    useEffect(() => {
        setIsLoggedIn(!!(firstName && lastName));
    }, [firstName, lastName]);

    const handleLogout = () => {
        localStorage.removeItem('currentUser');
        updateKey();
    };

    return (
        <nav className="navbar">
            <ul className="navbar-list">
                {/* Display first and last name if available */}
                {isLoggedIn && (
                    <li className="navbar-item">
                        {firstName} {lastName}
                    </li>
                )}
                <li className="navbar-item">
                    <Link to="/">Homepage</Link>
                </li>
                {!isLoggedIn && (
                    <>
                        <li className="navbar-item">
                            <Link to="/login">Login</Link>
                        </li>
                        <li className="navbar-item">
                            <Link to="/signup">Signup</Link>
                        </li>
                    </>
                )}
                {isLoggedIn && (
                    <>
                        <li className="navbar-item">
                            <Link to="/companies">Companies</Link>
                        </li>
                        <li className="navbar-item">
                            <Link to="/jobs">Jobs</Link>
                        </li>
                        <li className="navbar-item">
                            <Link to="/users">Users</Link>
                        </li>
                        <li className="navbar-item">
                            <Link to="/profileeditpage">Profile Edit Page</Link>
                        </li>
                        <li className="navbar-item">
                            <Link to="/logoutconfirmation" onClick={handleLogout}>Logout</Link>
                        </li>
                    </>
                )}
            </ul>
        </nav>
    );
};

export default NavBar;
