import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import JoblyContext from "../JoblyContext";
import useConfirmLoggedIn from "../Helpers/useConfirmLoggedIn";

const ProfileEditPage = () => {
    useConfirmLoggedIn();

    const JoblyApi = useContext(JoblyContext);
    const navigate = useNavigate();

    // Retrieve user data from localStorage
    const storedUserData = JSON.parse(localStorage.getItem("currentUser")) || {};

    // Initialize state with user data fetched from localStorage
    const [userData, setUserData] = useState({
        username: storedUserData.username || "", 
        first_name: storedUserData.first_name || "",
        last_name: storedUserData.last_name || "",
        email: storedUserData.email || "",
        password: "", // Assuming you don't want to populate password field
        error: ""
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Call the updateUser function with updated user data
            await JoblyApi.updateUser(userData);
            // Redirect to profile page after successful update
            navigate("/profile");
        } catch (error) {
            setUserData((prevData) => ({
                ...prevData,
                error: error.message
            }));
        }
    };

    return (
        <div>
            <section className="section-container"> 
                <form className="form-container" onSubmit={handleSubmit}>
                    <div className="form-item">
                        <label htmlFor="username">Username</label> 
                        <input 
                            id="username" 
                            type="text" 
                            name="username" 
                            value={userData.username} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="first_name">First Name</label> 
                        <input 
                            id="first_name" 
                            type="text" 
                            name="first_name" 
                            value={userData.first_name} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="last_name">Last Name</label> 
                        <input 
                            id="last_name" 
                            type="text" 
                            name="last_name" 
                            value={userData.last_name} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="email">Email</label> 
                        <input 
                            id="email" 
                            type="text" 
                            name="email" 
                            value={userData.email} 
                            onChange={handleChange} />
                    </div>


                    {/* Add other fields (firstName, lastName, email) similarly */}
                    <button type="submit">Update</button>
                </form>
                {userData.error && (
                    <p>{userData.error}</p>
                )}
            </section>
        </div>
    );
};

export default ProfileEditPage;
