import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import JoblyContext from "../context/JoblyContext";
import useConfirmLoggedIn from "../Helpers/useConfirmLoggedIn";

const ProfileEditPage = () => {
    useConfirmLoggedIn();

    const JoblyApi = useContext(JoblyContext);
    const navigate = useNavigate();

    const initialState = {
        username: localStorage.getItem("username") || "", 
        firstName: localStorage.getItem("firstName") || "",
        lastName: localStorage.getItem("lastName") || "",
        email: localStorage.getItem("email") || "",
        password: "", // Don't store password in localStorage
        error: ""
    };

    const [userData, setUserData] = useState(initialState);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Call the updateUser function with updated user data
            await JoblyApi.updateUser(userData);
            // Redirect to profile page after successful update
            navigate("/profile");
        } catch (error) {
            setUserData((prevData) => ({
                ...prevData,
                error: error.message
            }));
        }
    };

    return (
        <div>
            <section className="section-container"> 
                <form className="form-container" onSubmit={handleSubmit}>
                    <div className="form-item">
                        <label htmlFor="username">Username</label> 
                        <input 
                            id="username" 
                            type="text" 
                            name="username" 
                            value={userData.username} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="firstName">First Name</label> 
                        <input 
                            id="firstName" 
                            type="text" 
                            name="firstName" 
                            value={userData.firstName} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="username">Last Name</label> 
                        <input 
                            id="lastName" 
                            type="text" 
                            name="lastName" 
                            value={userData.lastName} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="username">Email</label> 
                        <input 
                            id="email" 
                            type="text" 
                            name="email" 
                            value={userData.email} 
                            onChange={handleChange} />
                    </div>
                    <div className="form-item">
                        <label htmlFor="username">Password</label> 
                        <input 
                            id="password" 
                            type="password" 
                            name="password" 
                            value={userData.password} 
                            onChange={handleChange} />
                    </div>
                    {/* Add other fields (firstName, lastName, email) similarly */}
                    <button type="submit">Update</button>
                </form>
                {userData.error && (
                    <p>{userData.error}</p>
                )}
            </section>
        </div>
    );
};

export default ProfileEditPage;
