import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Logout = ({ logoutKey }) => {
    const initialState = localStorage.getItem('currentUser');
    const [confirmLogout, setConfirmLogout] = useState(initialState);
    const navigate = useNavigate();


    const handleSubmit = async (event) => {
        try{
        console.log("Before", confirmLogout)
        event.preventDefault();
        localStorage.removeItem('currentUser');
        setConfirmLogout(null);
        navigate('/logoutconfirmation'); // Redirect after successful logout
        logoutKey(); // Update the key in the parent component
        } catch (error) {
            console.error("Error logging out", error)
        } console.log("After", confirmLogout)
    };

    return (
        <div>
            <section className="section-container"> 
                <form className="form-container" onSubmit={handleSubmit}>
                    <button type="submit">Logout</button>
                </form>
            </section>
        </div>
    );
};

export default Logout;
