import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import JoblyContext from "../JoblyContext";

const Logout = ({ loginKey }) => {
    const JoblyApi = useContext(JoblyContext);
    const navigate = useNavigate();

    const initialState = {
        username: localStorage.getItem("username") || "", // Change response to username
        error: ""
    };

    const [logoutData, setLogoutData] = useState(initialState);
    const [formSubmitted, setFormSubmitted] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setLogoutData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    useEffect(() => {
        // Update localStorage when username changes
        localStorage.setItem("username", logoutData.username);
    }, [logoutData.username]);

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {        
            await JoblyApi.logoutUser(logoutData); // Call logoutUser function
            navigate('/'); // Redirect after successful logout
            loginKey(); // Update the key in the parent component
            setFormSubmitted(true); // Set formSubmitted to true after successful submission
        } catch (error) {
            setLogoutData((prevData) => ({
                ...prevData,
                error: "Error logging out"
            }));
        }
    };

    return (
        <div>
            <section className="section-container"> 
                <form className="form-container" onSubmit={handleSubmit}>
                    <div className="form-item">
                        <label htmlFor="username">Username</label> {/* Change Response to Username */}
                        <input 
                            id="username" 
                            type="text" 
                            name="username" 
                            value={logoutData.username} 
                            onChange={handleChange} />
                    </div>
                    <button type="submit">Logout</button>
                </form>
                {formSubmitted && (
                    <p>Form submitted successfully!</p>
                )}
            </section>
        </div>
    );
};

export default Logout;
